import React, { useState, useEffect } from 'react';
import api from '../utils/api_config.js'

function Main(props) {

    const [userName, getUserName] = useState('');
    const [userDescription, getUserDescription] = useState('');
    const [userAvatar, getUserAvatar] = useState('');

    const [cards, getCardsFromServer] = useState([]);

    useEffect(() => {
        api.getUser()
            .then((data) => {
                getUserName(data.name);
                getUserDescription(data.about);
                getUserAvatar(data.avatar)
            })
            .catch((err) => {
                console.log(err)
            })
    },
        []);

    useEffect(() => {
        api.getAllCards()
            .then((data) => {
                getCardsFromServer(data);
            })
            .catch((err) => {
                console.log(err)
            })
    },
        [])


    return (
        <main className="main page__main">
            <section className="profile main__profile">
                <div onClick={props.onEditAvatar} className="profile__wrapper">
                    <img
                        className="profile__avatar"
                        src={userAvatar}
                        alt="Аватар профиля."
                    />
                </div>
                <div className="profile__info">
                    <h1 className="profile__name">{userName}</h1>
                    <button onClick={props.onEditProfile} className="profile__edit-button" type="button" />
                    <h2 className="profile__description">{userDescription}</h2>
                </div>
                <button onClick={props.onAddPlace} className="profile__add-button" type="button" />
            </section>
            <section className="main__gallery">
                <ul className="gallery">
                    <For each="card" in={cards}>
                        <li key={card.id} className="gallery__item">
                            <button className="gallery__delete-item" type="button"></button>
                            <img className="gallery__image" src={card.link} alt={card.name}/>
                                <div className="gallery__item-content">
                                    <h2 className="gallery__title">{card.name}</h2>
                                    <div className="gallery__like_container">
                                        <button className="gallery__like" type="button"></button>
                                        <span className="gallery__like-counter"></span></div>
                                </div>
                        </li>
                    </For>
                </ul>
            </section>
        </main>
    )

}

export default Main
