import React from 'react';
import '../index.css';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.js';

class App extends React.Component() {

  render(){
  return (

    <div className="page">
      <Header />
      <Main onEditAvatar={handleEditAvatarClick} onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick}/>
      <Footer />
      <PopupWithForm title="Редактировать профиль" name="profile-edit">
      <form id="profile" class="popup__form popup__form_edit-profile" name="edit-profile" novalidate>
        <input id="profile-name-input" class="popup__input popup__input_type_name" type="text" name="name"
          placeholder="Имя" required minlength="2" maxlength="40"/>
        <span class="profile-name-input-error"></span>
        <input id="job-input" class="popup__input popup__input_type_job" type="text" name="profession"
          placeholder="Профессия" required minlength="2" maxlength="200"/>
        <span class="job-input-error"></span>
        <button class="popup__save-button popup__button" type="submit">Сохранить</button>
      </form>
        </PopupWithForm>
      <PopupWithForm title="Новое место" name="add-card">
      <form id="gallery" class="popup__form popup__form_add-card" name="add-card" novalidate>
        <input id="card-title-input" class="popup__input popup__input_type_card-title" type="text" name="card_title"
          placeholder="Название" required minlength="2" maxlength="30"/>
        <span class="card-title-input-error"></span>
        <input id="image-link-input" class="popup__input popup__input_type_image-link" type="url" name="image_link"
          placeholder="Ссылка на картинку" required/>
        <span class="image-link-input-error"></span>
        <button class="popup__create-button popup__button" type="submit">Создать</button>
      </form>
        </PopupWithForm>
      <ImagePopup />
      <PopupWithForm title="Вы уверены?" name="delete-card">
      <button class="popup__confirm-button popup__button popup__button_active popup__button-yes" type="submit">Да</button>
        </PopupWithForm>
      <PopupWithForm title="Обновить аватар" name="new-avatar">
      <form id="avatar" class="popup__form popup__form_new-avatar" name="new-avatar" novalidate>
        <input id="avatar-link-input" class="popup__input popup__input_type_avatar-image-link" type="url" name="avatar_link"
          placeholder="Ссылка на картинку" required/>
        <span class="avatar-link-input-error"></span>
        <button class="popup__save-button popup__button" type="submit">Сохранить</button>
      </form>
      </PopupWithForm>

      <template className="gallery-template" />
    </div>
  );

  function handleEditAvatarClick() {
    const avatarPopup = document.querySelector('.popup-new-avatar');
    avatarPopup.classList.add('popup_is-opened');
  }

  function handleEditProfileClick() {
    const profilePopup = document.querySelector('.popup-profile-edit');
    profilePopup.classList.add("popup_is-opened");
  }

  function handleAddPlaceClick() {
    const addCardPopup = document.querySelector('.popup-add-card');
    addCardPopup.classList.add('popup_is-opened');
  }
  }
}

export default App;
