import React from 'react';
import '../index.css';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.js';

class App extends React.Component {
  render(){
  return (

    <div className="page">
      <Header />
      <Main onEditAvatar={handleEditAvatarClick} onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} isEditAvatarPopupOpen='false' isAddPlacePopupOpen='false' isEditProfilePopupOpen='false'/>
      <Footer />
      <PopupWithForm title="Редактировать профиль" name="profile-edit">
      <form id="profile" className="popup__form popup__form_edit-profile" name="edit-profile" noValidate>
        <input id="profile-name-input" className="popup__input popup__input_type_name" type="text" name="name"
          placeholder="Имя" required minLength="2" maxLength="40"/>
        <span className="profile-name-input-error"></span>
        <input id="job-input" className="popup__input popup__input_type_job" type="text" name="profession"
          placeholder="Профессия" required minLength="2" maxLength="200"/>
        <span className="job-input-error"></span>
        <button className="popup__save-button popup__button" type="submit">Сохранить</button>
      </form>
        </PopupWithForm>
      <PopupWithForm title="Новое место" name="add-card">
      <form id="gallery" className="popup__form popup__form_add-card" name="add-card" noValidate>
        <input id="card-title-input" className="popup__input popup__input_type_card-title" type="text" name="card_title"
          placeholder="Название" required minLength="2" maxLength="30"/>
        <span className="card-title-input-error"></span>
        <input id="image-link-input" className="popup__input popup__input_type_image-link" type="url" name="image_link"
          placeholder="Ссылка на картинку" required/>
        <span className="image-link-input-error"></span>
        <button className="popup__create-button popup__button" type="submit">Создать</button>
      </form>
        </PopupWithForm>
      <ImagePopup />
      <PopupWithForm title="Вы уверены?" name="delete-card">
      <button className="popup__confirm-button popup__button popup__button_active popup__button-yes" type="submit">Да</button>
        </PopupWithForm>
      <PopupWithForm title="Обновить аватар" name="new-avatar">
      <form id="avatar" className="popup__form popup__form_new-avatar" name="new-avatar" noValidate>
        <input id="avatar-link-input" className="popup__input popup__input_type_avatar-image-link" type="url" name="avatar_link"
          placeholder="Ссылка на картинку" required/>
        <span className="avatar-link-input-error"></span>
        <button className="popup__save-button popup__button" type="submit">Сохранить</button>
      </form>
      </PopupWithForm>

      <template className="gallery-template" />
    </div>
  );
  }
  handleEditAvatarClick() {
    const avatarPopup = document.querySelector('.popup-new-avatar');
    avatarPopup.classNameList.add('popup_is-opened');
  }

 handleEditProfileClick() {
    const profilePopup = document.querySelector('.popup-profile-edit');
    profilePopup.classNameList.add("popup_is-opened");
  }

 handleAddPlaceClick() {
    const addCardPopup = document.querySelector('.popup-add-card');
    addCardPopup.classNameList.add('popup_is-opened');
  }

}

export default App;
